import React from "react";
import { minutesToDuration, secondsToDuration } from "../utils/duration";

function TimerDisplay({ session, durations, isTimerRunning }) {
    let elapsedTime = 0;
    const focusTime = durations.focus * 60;
    const breakTime = breakDuration * 60;
    if (session && session.label === "Focusing") {
        elapsedTime = ((focusTime - session.timeRemaining) / focusTime) * 100;
    } else if (session && session.label === "On Break") {
        elapsedTime = ((breakTime - session.timeRemaining) / breakTime) * 100;
    }
    if (session) {
        return (<>
            <div className="row mb-2">
                <div className="col">
                    {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
                    <h2 data-testid="session-title">
                        {session?.label} for {
                            session.label === "Focusing" ? minutesToDuration(focusDuration) :
                                minutesToDuration(breakDuration)} minutes
                    </h2>
                    {/* TODO: Update message below correctly format the time remaining in the current session */}
                    <p className="lead" data-testid="session-sub-title">
                        {secondsToDuration(session?.timeRemaining)} remaining
                    </p>
                </div>
            </div>
            <div className="row mb-2">
                <div className="col">
                    <div className="progress" style={{ height: "20px" }}>
                        <div
                            className="progress-bar"
                            role="progressbar"
                            aria-valuemin="0"
                            aria-valuemax="100"
                            aria-valuenow={elapsedTime} // TODO: Increase aria-valuenow as elapsed time increases
                            style={{ width: `${elapsedTime}%` }} // TODO: Increase width % as elapsed time increases
                        />
                    </div>
                </div>
            </div>
            <div>
                {isTimerRunning ? "" : <h2>PAUSED</h2>}
            </div>
        </>);
    }
    else return <div></div>;

}

export default TimerDisplay;
